import React, { useState } from 'react';
import MovieList from './components/MovieList';
import './App.css';

const App = () => {
  const initialMovies = [
    {
      id: 1,
      title: 'Good Will Hunting',
      synopsis: 'Will Hunting (Matt Damon) has a genius-level IQ but chooses to work as a janitor at MIT. When he solves a difficult graduate-level math problem, his talents are discovered by Professor Gerald Lambeau (Stellan Skarsgard), who decides to help the misguided youth reach his potential. When Will is arrested for attacking a police officer, Professor Lambeau makes a deal to get leniency for him if he will get treatment from therapist Sean Maguire (Robin Williams).',
      image: 'data:image/jpeg;base64,',
      rating: 4,
      reviews: [
        { id: 1, text: 'Amazing movie!', rating: 5 },
        { id: 2, text: 'Mind-blowing.', rating: 4 }
      ]
    },
    {
      id: 2,
      title: 'Donnie Darko',
      synopsis: 'During the presidential election of 1988, a teenager named Donnie Darko sleepwalks out of his house one night and sees a giant, demonic-looking rabbit named Frank, who tells him the world will end in 28 days. When Donnie returns home, he finds that a jet engine has crashed into his bedroom. Is Donnie living in a parallel universe, is he suffering from mental illness - or will the world really end?',
      image: 'data:image/jpeg;base64,',
      rating: 5,
      reviews: [
        { id: 1, text: 'A masterpiece.', rating: 5 },
        { id: 2, text: 'Great visuals.', rating: 4 }
      ]
    }
  ];

  const [movies, setMovies] = useState(initialMovies);

  const addReview = (movieId, review) => {
    setMovies(movies.map(movie => 
      movie.id === movieId 
        ? { ...movie, reviews: [...movie.reviews, review] }
        : movie
    ));
  };

  const updateRating = (movieId, rating) => {
    setMovies(movies.map(movie => 
      movie.id === movieId 
        ? { ...movie, rating }
        : movie
    ));
  };

  return (
    <div className="App">
      <h1>Movie Reviews</h1>
      <MovieList movies={movies} addReview={addReview} updateRating={updateRating} />
    </div>
  );
};

export default App;
